#include <stdio.h>
#include <limits.h>

#define N 510

int n, m, f[N][N], dis[N], vis[N];
long long a[N][N], ans;

void bellman_ford(int s) {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            f[i][j] = INT_MAX;
        }
    }
    for (int i = 0; i < n; i++) {
        f[i][i] = 0;
    }
    for (int k = 0; k < n; k++) {
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                f[i][j] = f[i][j] < f[i][k] + f[k][j] ? f[i][j] : f[i][k] + f[k][j];
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            f[i][j] = f[i][j] < f[j][i] + a[j][i] ? f[i][j] : f[j][i] + a[j][i];
        }
    }
    for (int i = 0; i < n; i++) {
        if (f[i][i] < 0) {
            printf("-1\n");
            exit(0);
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dis[j] = f[i][j];
        }
        for (int j = 0; j < n; j++) {
            if (dis[j] < INT_MAX && dis[j] != f[i][j]) {
                printf("-1\n");
                exit(0);
            }
        }
    }
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            if (f[i][j] < dis[j]) {
                ans += a[i][j];
            }
        }
    }
}

