import sys
import heapq

def bellman_ford(graph, start):
    distance = [float('inf')] * len(graph)
    distance[start] = 0
    for _ in range(len(graph) - 1):
        for u in range(len(graph)):
            for v, w in graph[u]:
                if distance[u] + w < distance[v]:
                    distance[v] = distance[u] + w
    return distance

def bellman_ford_negative_cycle(graph):
    distance = [float('inf')] * len(graph)
    for _ in range(len(graph) - 1):
        for u in range(len(graph)):
            for v, w in graph[u]:
                if distance[u] + w < distance[v]:
                    return True
    return False

def dijkstra(graph, start):
    distance = [float('inf')] * len(graph)
    distance[start] = 0
    pq = [(0, start)]
    while pq:
        d, u = heapq.heappop(pq)
        if d > distance[u]:
            continue
        for v, w in graph[u]:
            if distance[u] + w < distance[v]:
                distance[v] = distance[u] + w
                heapq.heappush(pq, (distance[v], v))
    return distance

def solve():
    n = int(sys.stdin.readline())
    graph = [[] for _ in range(n)]
    for i in range(n):
        row = list(map(int, sys.stdin.readline().split()))
        for j in range(n):
            if i != j:
                graph[i].append((j, -1 if i < j else 1))
            if i < n - 1:
                graph[i].append((i + 1, 0))
    for i in range(n):
        for j in range(n):
            if i != j:
                graph[i].append((j, row[i * n + j]))
    if bellman_ford_negative_cycle(graph):
        print(-1)
        return
    distance = bellman_ford(graph, 0)
    cost = 0
    for i in range(n):
        for j in range(n):
            if i != j and distance[i] + graph[i][j][1] < distance[j]:
                cost += graph[i][j][1]
    print(cost)

solve()
