#include <iostream>
#include <string>
#include <vector>

using namespace std;

int main() {
    string s;
    cin >> s;

    int n = s.length();
    int cnt[3] = {0};
    for (int i = 0; i < n; i++) {
        cnt[s[i] - 'A']++;
    }

    int max_len = 0;
    string ans;
    for (int i = 0; i < n; i++) {
        int j = i;
        int k = 0;
        while (j < n && k < 3) {
            if (cnt[s[j] - 'A'] == cnt[s[i] - 'A']) {
                if (j > i) {
                    ans += s[j - 1];
                }
                i = j;
                k++;
            }
            j++;
        }
        if (k > max_len) {
            max_len = k;
            ans = "";
            for (int l = 0; l < k; l++) {
                ans += s[i + l];
            }
        }
    }

    cout << ans << endl;

    return 0;
}
