import java.io.*;
import java.util.*;

public class Main {
    static final int N = (int) 1e6 + 10;
    static final int M = 3;
    static int n;
    static char[] s = new char[N];
    static int[] cnt = new int[M];
    static int[] nxt = new int[N];
    static int[] dp = new int[N];
    static int[] pre = new int[N];
    static int[] pos = new int[N];
    static int[] ans = new int[N];

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        n = sc.next().length();
        for (int i = 0; i < n; i++) {
            s[i] = sc.next().charAt(i);
            cnt[s[i] - 'A']++;
        }
        for (int i = 0; i < n; i++) {
            if (i > 0 && s[i] == s[i - 1]) {
                nxt[i] = nxt[i - 1];
            } else {
                nxt[i] = i;
            }
        }
        for (int i = 0; i < n; i++) {
            if (i > 0 && dp[i] == dp[i - 1]) {
                pre[i] = pre[i - 1];
            } else {
                pre[i] = i;
            }
            if (i > 0 && dp[i] == dp[i - 1] && s[i] == s[i - 1]) {
                pos[i] = pos[i - 1];
            } else {
                pos[i] = i;
            }
            if (i > 0 && dp[i] == dp[i - 1] && s[i] == s[i - 1] && nxt[i - 1] == i - 1) {
                dp[i] = dp[i - 1];
            } else {
                dp[i] = dp[i - 1] + 1;
            }
            if (dp[i] == cnt[0] && dp[i] == cnt[1] && dp[i] == cnt[2]) {
                int j = i;
                while (j > 0 && s[j] == s[j - 1]) {
                    j--;
                }
                int k = j;
                while (k > 0 && s[k] == s[k - 1]) {
                    k--;
                }
                if (k > 0 && dp[j] == dp[k - 1] && nxt[k - 1] == j - 1) {
                    for (int l = j; l <= i; l++) {
                        ans[l - j + k - 1] = s[l];
                    }
                    for (int l = 0; l < i - j + k; l++) {
                        System.out.print(ans[l]);
                    }
                    System.out.println();
                    return;
                }
            }
        }
        System.out.println();
    }
}
