import java.io.*;
import java.util.*;

public class Main {
    static final int N = 500;
    static final int M = 1000000007;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        int mod = Integer.parseInt(st.nextToken());

        long[][] dp = new long[n * 2 + 1][n * 2 + 1];
        long[][] dp2 = new long[n * 2 + 1][n * 2 + 1];

        for (int i = 0; i <= n * 2; i++) {
            for (int j = 0; j <= n * 2; j++) {
                dp[i][j] = 0;
                dp2[i][j] = 0;
            }
        }

        dp[0][0] = 1;

        for (int i = 0; i <= n * 2; i++) {
            for (int j = 0; j <= n * 2; j++) {
                if (i + j <= n * 2) {
                    dp2[i + j][i] = (dp2[i + j][i] + dp[i][j]) % mod;
                }
                if (i - j >= 0) {
                    dp2[i - j][i] = (dp2[i - j][i] + dp[i][j]) % mod;
                }
                if (j + i <= n * 2) {
                    dp2[j + i][j] = (dp2[j + i][j] + dp[i][j]) % mod;
                }
                if (j - i >= 0) {
                    dp2[j - i][j] = (dp2[j - i][j] + dp[i][j]) % mod;
                }
            }
        }

        long ans = 0;
        for (int i = 0; i <= n * 2; i++) {
            for (int j = 0; j <= n * 2; j++) {
                if (n * n <= i * i + j * j && (2 * n) * (2 * n) >= i * i + j * j) {
                    ans = (ans + dp2[i][j]) % mod;
                }
            }
        }

        pw.println(ans);
        pw.close();
    }
}
