import sys
input = sys.stdin.readline

def read_int():
    return int(input())

def read_ints():
    return map(int, input().split())

def read_int_list():
    return list(map(int, input().split()))

def read_string():
    return input().strip()

def read_string_list():
    return input().strip().split()

def solve():
    N, M = read_ints()
    if N == 1:
        print(1)
        return
    dp = [[0] * (N * 2 + 1) for _ in range(N * 2 + 1)]
    dp[0][0] = 1
    for i in range(N * 2):
        for j in range(N * 2):
            if dp[i][j]:
                for k in range(N * 2):
                    if i + k < N * 2 and j + k < N * 2:
                        dp[i + k][j + k] = (dp[i + k][j + k] + dp[i][j]) % M
    ans = 0
    for i in range(N * 2):
        for j in range(N * 2):
            if i + j < N * 2:
                ans = (ans + dp[i][j] * dp[i + j][N * 2 - 1]) % M
    print(ans)

if __name__ == '__main__':
    T = 1
    for _ in range(T):
        solve()
