#include <stdio.h>
#include <stdlib.h>

#define MAX_N 200005

int n;
int a[MAX_N];
int prime[MAX_N];
int cnt[MAX_N];
int vis[MAX_N];
int ans[MAX_N];

int main() {
    int i, j, k;
    int p, q;

    scanf("%d", &n);
    for (i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }

    for (i = 2; i <= n; i++) {
        if (!vis[i]) {
            prime[++prime[0]] = i;
            cnt[i] = 1;
        }
        for (j = 1; j <= prime[0] && i * prime[j] <= n; j++) {
            vis[i * prime[j]] = 1;
            if (i % prime[j] == 0) {
                cnt[i * prime[j]] = cnt[i];
                break;
            }
            cnt[i * prime[j]] = cnt[i] + 1;
        }
    }

    for (i = 1; i <= n; i++) {
        if (a[i] == 0) {
            ans[++ans[0]] = i;
        }
    }

    for (i = 1; i <= n; i++) {
        if (a[i] == 1) {
            for (j = 1; j <= prime[0] && prime[j] <= n / i; j++) {
                if (cnt[i * prime[j]] % 2 == 0) {
                    ans[++ans[0]] = i * prime[j];
                    break;
                }
            }
        }
    }

    if (ans[0] == 0) {
        printf("-1\n");
    } else {
        printf("%d\n", ans[0]);
        for (i = 1; i <= ans[0]; i++) {
            printf("%d ", ans[i]);
        }
        printf("\n");
    }

    return 0;
}
