#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

const int MAXN = 2e5 + 5;

int N;
int a[MAXN];
vector<int> b;

void solve() {
    cin >> N;
    for (int i = 1; i <= N; i++) {
        cin >> a[i];
    }

    for (int i = 1; i <= N; i++) {
        for (int j = i; j <= N; j += i) {
            if (a[j] == 1) {
                b.push_back(j);
                break;
            }
        }
    }

    if (b.size() == 0) {
        cout << -1 << endl;
    } else {
        sort(b.begin(), b.end());
        cout << b.size() << endl;
        for (int i = 0; i < b.size(); i++) {
            cout << b[i] << " ";
        }
        cout << endl;
    }
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int T = 1;
    // cin >> T;
    while (T--) {
        solve();
    }

    return 0;
}
