#include <stdio.h>
#include <stdlib.h>

int main() {
    int n;
    scanf("%d", &n);

    int* a = (int*)malloc(n * sizeof(int));
    for (int i = 0; i < n; i++) {
        scanf("%d", &a[i]);
    }

    int colors = 0;
    int max_colors = 0;
    for (int i = 0; i < n; i++) {
        int color = 0;
        for (int j = 0; j < i; j++) {
            if (a[j] < a[i] && color == 0) {
                color = a[j];
            } else if (a[j] < a[i] && color != 0 && color == a[j]) {
                color = a[j] + 1;
            }
        }
        if (color == 0) {
            color = a[i];
        }
        colors = (color > colors) ? color : colors;
        max_colors = (colors > max_colors) ? colors : max_colors;
    }

    printf("%d\n", max_colors + 1);

    free(a);
    return 0;
}
