#include <stdio.h>
#include <stdlib.h>

#define MOD 1000000007

long long factorial[55];
long long inverse[55];

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        b >>= 1;
    }
    return res;
}

long long ncr(long long n, long long r) {
    if (r > n) return 0;
    return (factorial[n] * inverse[r] % MOD * inverse[n - r] % MOD);
}

long long catalan(long long n, long long k) {
    if (k > n) return 0;
    if (k == 0 || k == n) return 1;
    return (ncr(n, k) - catalan(n - 1, k - 1) - catalan(n - 1, k)) % MOD;
}

int main() {
    int n, k;
    scanf("%d %d", &n, &k);
    factorial[0] = 1;
    for (int i = 1; i <= n; i++) {
        factorial[i] = (factorial[i - 1] * i) % MOD;
    }
    inverse[n] = power(factorial[n], MOD - 2);
    for (int i = n - 1; i >= 0; i--) {
        inverse[i] = (inverse[i + 1] * (i + 1)) % MOD;
    }
    long long res = 0;
    for (int i = 0; i <= n; i++) {
        res = (res + catalan(n, i) * catalan(n - i, k - i * (n - i))) % MOD;
    }
    printf("%lld\n", res);
    return 0;
}
