#include <iostream>
#include <vector>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long C(int n, int m) {
    if (m > n) return 0;
    long long res = 1;
    for (int i = 1; i <= m; i++) {
        res = res * (n - i + 1) % MOD;
        res = res * power(i, MOD - 2) % MOD;
    }
    return res;
}

long long f[55][55];

void solve() {
    int n, k;
    cin >> n >> k;
    f[0][0] = 1;
    for (int i = 1; i <= n; i++) {
        for (int j = 0; j <= n * n; j++) {
            if (j >= i) {
                f[i][j] = (f[i - 1][j - i] + (j % 2 == 0 ? C(i, i - (j - i) / 2) : 0)) % MOD;
            }
            else {
                f[i][j] = 0;
            }
        }
    }
    cout << f[n][k] << endl;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(0);
    int t = 1;
    while (t--) {
        solve();
    }
    return 0;
}
