#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 20

int dp[1 << 16][MAXN];
int n, q, l[16], r[16];

int calc(int x) {
    int res = 0;
    while (x) {
        res += x % 10;
        x /= 10;
    }
    return res;
}

int main() {
    scanf("%d%d", &n, &q);
    for (int i = 0; i < q; i++) {
        scanf("%d%d", &l[i], &r[i]);
        l[i]--;
    }

    for (int i = 0; i < (1 << q); i++) {
        for (int j = 0; j <= n; j++) {
            dp[i][j] = 0;
        }
    }

    dp[0][0] = 1;

    for (int i = 0; i < (1 << q); i++) {
        for (int j = 0; j <= n; j++) {
            if (dp[i][j]) {
                for (int k = 0; k < q; k++) {
                    if (i & (1 << k)) {
                        int len = r[k] - l[k] + 1;
                        if (j + len <= n) {
                            int x = j + len;
                            int y = calc(j) % 9;
                            int z = calc(x) % 9;
                            if (z % 9 == y) {
                                dp[i ^ (1 << k)][x] = (dp[i ^ (1 << k)][x] + dp[i][j]) % MOD;
                            }
                        }
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[(1 << q) - 1][i]) % MOD;
    }

    printf("%d\n", ans);

    return 0;
}
