import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int day = scanner.nextInt();
        int year = 2017;
        int month = 8; // September is the 9th month, but Java uses 0-based indexing

        int[] daysInMonth = {31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};
        int[] daysOfWeek = {0, 3, 2, 5, 0, 3, 5, 1, 4, 6, 2, 4};

        int totalDays = 0;
        for (int i = 0; i < month; i++) {
            totalDays += daysInMonth[i];
        }
        totalDays += day - 1;

        int dayOfWeek = (totalDays + year + year / 4 - year / 100 + year / 400) % 7;
        System.out.println(getDayOfWeek(dayOfWeek));
    }

    private static String getDayOfWeek(int dayOfWeek) {
        switch (dayOfWeek) {
            case 0:
                return "sun";
            case 1:
                return "mon";
            case 2:
                return "tue";
            case 3:
                return "wed";
            case 4:
                return "thu";
            case 5:
                return "fri";
            case 6:
                return "sat";
            default:
                return "";
        }
    }
}
