#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 20;
const int M = 1e9 + 7;

int n, q;
int l[N], r[N];
int dp[N][1 << N];
int pw[N];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> q;
    for (int i = 0; i < q; i++) {
        cin >> l[i] >> r[i];
    }

    pw[0] = 1;
    for (int i = 1; i <= n; i++) {
        pw[i] = (pw[i - 1] * 10) % M;
    }

    for (int i = 0; i <= n; i++) {
        for (int mask = 0; mask < (1 << q); mask++) {
            dp[i][mask] = 0;
        }
    }

    dp[0][0] = 1;

    for (int i = 0; i < n; i++) {
        for (int mask = 0; mask < (1 << q); mask++) {
            for (int j = 0; j < q; j++) {
                if ((mask >> j) & 1) {
                    int len = r[j] - l[j] + 1;
                    if (i + len <= n) {
                        int x = (i + len) / 10;
                        int y = (i + len) % 10;
                        int z = (i + len) / 9;
                        int w = (i + len) % 9;
                        if (z <= x && w <= y) {
                            dp[i + len][mask ^ (1 << j)] = (dp[i + len][mask ^ (1 << j)] + dp[i][mask] * pw[len]) % M;
                        }
                    }
                }
            }
        }
    }

    int ans = 0;
    for (int mask = 0; mask < (1 << q); mask++) {
        ans = (ans + dp[n][mask]) % M;
    }

    cout << ans << '\n';

    return 0;
}
