import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = (int) 1e9 + 7;
    static final int MAXN = 20;
    static int[][][] dp = new int[MAXN][MAXN][1 << MAXN];
    static int[][][] sum = new int[MAXN][MAXN][1 << MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        int q = Integer.parseInt(br.readLine());

        int[][] intervals = new int[q][2];
        for (int i = 0; i < q; i++) {
            st = new StringTokenizer(br.readLine());
            intervals[i][0] = Integer.parseInt(st.nextToken());
            intervals[i][1] = Integer.parseInt(st.nextToken());
        }

        for (int i = 0; i < MAXN; i++) {
            for (int j = 0; j < MAXN; j++) {
                for (int k = 0; k < (1 << MAXN); k++) {
                    dp[i][j][k] = -1;
                    sum[i][j][k] = -1;
                }
            }
        }

        pw.println(solve(n, q, intervals));
        pw.close();
    }

    static int solve(int n, int q, int[][] intervals) {
        int ans = 0;
        for (int i = 0; i <= n; i++) {
            for (int mask = 0; mask < (1 << q); mask++) {
                if (dp[i][q][mask] == -1) continue;
                for (int j = 0; j < q; j++) {
                    if ((mask & (1 << j)) != 0) {
                        int l = intervals[j][0] - 1;
                        int r = intervals[j][1] - 1;
                        if (l > i) continue;
                        int rem = i - l;
                        int cnt = 0;
                        while (rem > 0) {
                            cnt += rem % 10;
                            rem /= 10;
                        }
                        cnt %= 9;
                        if (cnt != 0) continue;
                        int newMask = mask ^ (1 << j);
                        if (j + 1 == q) {
                            ans = (ans + dp[i][q][mask]) % MOD;
                        } else {
                            ans = (ans + dp[i][j + 1][newMask]) % MOD;
                        }
                    }
                }
            }
        }
        return ans;
    }
}
