#include <stdio.h>
#include <stdlib.h>
#include <math.h>

typedef long long ll;

int main() {
    int N;
    scanf("%d", &N);
    ll x[N], y[N];
    for (int i = 0; i < N; i++) {
        scanf("%lld %lld", &x[i], &y[i]);
    }
    ll minX = x[0], minY = y[0];
    for (int i = 0; i < N; i++) {
        if (x[i] < minX) minX = x[i];
        if (y[i] < minY) minY = y[i];
    }
    ll maxX = x[0], maxY = y[0];
    for (int i = 0; i < N; i++) {
        if (x[i] > maxX) maxX = x[i];
        if (y[i] > maxY) maxY = y[i];
    }
    ll X = minX, Y = minY;
    for (int i = 0; i < N; i++) {
        if (x[i] == minX && y[i] == minY) {
            if ((maxX - minX) % 2 == 0 && (maxY - minY) % 2 == 0) {
                X = x[i];
                Y = y[i];
                break;
            }
        }
    }
    printf("%lld %lld\n", X, Y);
    return 0;
}
