import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int R = scanner.nextInt();
        int B = scanner.nextInt();
        int K = 0;

        if (R == B) {
            K = 1 + Math.min(R, B);
        } else {
            K = 1 + Math.min(R, B);
            int diff = Math.abs(R - B);
            int min = Math.min(R, B);
            while (diff > 0) {
                K++;
                diff--;
                min--;
            }
        }

        System.out.println(K);
    }
}
