#include <stdio.h>
#include <math.h>
#include <string.h>

#define MOD 1000000007
#define N 1000

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return power(a, MOD - 2);
}

int main() {
    int n;
    char s[N + 1];
    scanf("%d %s", &n, s);

    long long a = 0, b = 0, c = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'X') {
            a++;
            for (int j = 0; j < n; j++) {
                if (s[j] == 'X') {
                    if (j == i || j == i + 1) continue;
                    long long x = 1, y = 0;
                    for (int k = 0; k < n; k++) {
                        if (s[k] == 'X') {
                            if (k == j || k == j + 1) continue;
                            x = x * (k - j) % MOD;
                            y = (y + x) % MOD;
                        }
                    }
                    b = (b + x * inv(y)) % MOD;
                    c = (c + x * inv(y) * (i - j)) % MOD;
                }
            }
        }
    }

    long long p = (a * inv(2) - b * inv(2) * inv(4)) % MOD;
    long long q = (b * inv(4)) % MOD;
    long long r = (c * inv(4)) % MOD;

    printf("%lld %lld %lld\n", p, q, r);

    return 0;
}
