#include <iostream>
#include <vector>
#include <string>
#include <cmath>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long powmod(long long a, long long b) {
    long long res = 1;
    a %= MOD;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return powmod(a, MOD - 2);
}

long long C(int n, int k) {
    long long res = 1;
    for (int i = 0; i < k; i++) {
        res = res * (n - i) % MOD * inv(i + 1) % MOD;
    }
    return res;
}

int main() {
    int N;
    string s;
    cin >> N >> s;

    int X = 0;
    for (char c : s) {
        if (c == 'X') X++;
    }

    vector<long long> dp(N + 1);
    dp[0] = 1;
    for (int i = 0; i < N; i++) {
        vector<long long> ndp(N + 1);
        for (int j = 0; j <= N; j++) {
            if (j > 0) {
                ndp[j] = (ndp[j] + dp[j - 1] * (j - 1) % MOD * (N - j + 1) % MOD) % MOD;
            }
            if (j < N) {
                ndp[j] = (ndp[j] + dp[j + 1] * (j + 1) % MOD * (N - j)) % MOD;
            }
            if (j == 0 || j == N - 1) {
                ndp[j] = (ndp[j] + dp[j] * (j + 1) % MOD * (N - j)) % MOD;
            }
            if (j > 0 && j < N - 1) {
                ndp[j] = (ndp[j] + dp[j] * j % MOD * (N - j)) % MOD;
            }
        }
        dp = ndp;
    }

    long long p = 0;
    long long q = 0;
    long long r = 0;
    for (int i = 0; i <= N; i++) {
        long long x = dp[i];
        long long y = C(N, i);
        long long g = __gcd(x, y);
        x /= g;
        y /= g;
        p = (p + x) % MOD;
        q = (q + x * i % MOD * (N - i) % MOD) % MOD;
        r = (r + x * i % MOD * (N - i) % MOD * (N - i - 1) % MOD) % MOD;
    }

    long long e = 1;
    for (int i = 0; i < 100; i++) {
        e = e * 2.71828182845904523536028747135266 % MOD;
    }

    p = (p + MOD - 1) % MOD;
    q = (q + MOD - 1) % MOD;
    r = (r + MOD - 1) % MOD;
    q = q * inv(e) % MOD;
    r = r * inv(e) % MOD;
    r = r * inv(e) % MOD;

    cout << p << " " << q << " " << r << endl;

    return 0;
}
