import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 1005;

    static long[] fact = new long[MAXN];
    static long[] inv = new long[MAXN];

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) res = res * a % MOD;
            a = a * a % MOD;
            b >>= 1;
        }
        return res;
    }

    static long C(int n, int k) {
        return fact[n] * inv[k] % MOD * inv[n - k] % MOD;
    }

    static long invC(int n, int k) {
        return inv[n] * fact[k] % MOD * fact[n - k] % MOD;
    }

    static long[] solve(int n, String s) {
        long[] res = new long[3];
        long[] dp = new long[n + 1];
        dp[0] = 1;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'X') {
                for (int j = 0; j <= i; j++) {
                    dp[j + 1] = (dp[j + 1] + dp[j] * (i - j + 1) % MOD * (2 * C(i, j) - C(i - 1, j - 1)) % MOD) % MOD;
                }
            }
        }
        res[0] = dp[n];
        for (int i = 0; i <= n; i++) {
            dp[i] = (dp[i] + dp[i - 1]) % MOD;
        }
        long[] dp2 = new long[n + 1];
        dp2[0] = 1;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'X') {
                for (int j = 0; j <= i; j++) {
                    dp2[j + 1] = (dp2[j + 1] + dp2[j] * (i - j + 1) % MOD * (2 * C(i, j) - C(i - 1, j - 1)) % MOD) % MOD;
                }
            }
        }
        res[1] = dp2[n] * pow(2, n) % MOD;
        for (int i = 0; i <= n; i++) {
            dp2[i] = (dp2[i] + dp2[i - 1]) % MOD;
        }
        long[] dp3 = new long[n + 1];
        dp3[0] = 1;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'X') {
                for (int j = 0; j <= i; j++) {
                    dp3[j + 1] = (dp3[j + 1] + dp3[j] * (i - j + 1) % MOD * (2 * C(i, j) - C(i - 1, j - 1)) % MOD) % MOD;
                }
            }
        }
        res[2] = dp3[n] * pow(2, n) % MOD;
        for (int i = 0; i <= n; i++) {
            dp3[i] = (dp3[i] + dp3[i - 1]) % MOD;
        }
        return res;
    }

    public static void main(String[] args) throws IOException {
        Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        String s = sc.next();
        long[] res = solve(n, s);
        System.out.println((res[0] % MOD + MOD) % MOD);
        System.out.println(0);
        System.out.println((res[2] % MOD + MOD) % MOD);
    }

    static {
        fact[0] = 1;
        for (int i = 1; i < MAXN; i++) {
            fact[i] = fact[i - 1] * i % MOD;
        }
        inv[MAXN - 1] = pow(fact[MAXN - 1], MOD - 2);
        for (int i = MAXN - 2; i >= 0; i--) {
            inv[i] = inv[i + 1] * (i + 1) % MOD;
        }
    }
}

