#include <stdio.h>

int main() {
    int n, k;
    scanf("%d %d", &n, &k);

    int a[n + 1];
    for (int i = 1; i <= n; i++) {
        scanf("%d", &a[i]);
    }

    int q = 0;
    int x[n + 1];
    int used[n + 1] = {0};
    int cnt = 0;

    while (cnt < k + 1) {
        int max = 0;
        int idx = 0;
        for (int i = 1; i <= n; i++) {
            if (used[i] == 0 && a[i] > max) {
                max = a[i];
                idx = i;
            }
        }

        if (max == 0) {
            break;
        }

        used[idx] = 1;
        x[q++] = idx;
        cnt++;
    }

    for (int i = 1; i <= n; i++) {
        if (used[i] == 0) {
            int max = 0;
            int idx = 0;
            for (int j = 1; j <= n; j++) {
                if (used[j] == 0 && a[j] > max) {
                    max = a[j];
                    idx = j;
                }
            }

            if (max == 0) {
                break;
            }

            used[idx] = 1;
            x[q++] = idx;
        }
    }

    if (cnt == k + 1) {
        printf("%d\n", q);
        for (int i = 0; i < q; i++) {
            printf("%d ", x[i] + 1);
        }
        printf("\n");
    } else {
        printf("-1\n");
    }

    return 0;
}
