#include <stdio.h>
#include <stdbool.h>

int main() {
    int a, b, n, i, s, f;
    scanf("%d %d %d", &a, &b, &n);

    bool overlap = false;
    for (i = 0; i < n; i++) {
        scanf("%d %d", &s, &f);
        if (s < b && f > a) {
            overlap = true;
            break;
        }
    }

    printf("%d\n", overlap ? 1 : 0);

    return 0;
}
