#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;

    vector<int> a(n);
    for (int i = 0; i < n; i++) {
        cin >> a[i];
    }

    vector<int> b(n);
    for (int i = 0; i < n; i++) {
        b[i] = i;
    }

    sort(b.begin(), b.end(), [&](int i, int j) {
        return a[i] > a[j];
    });

    vector<int> ans;
    for (int i = 0; i < n; i++) {
        if (i < k) {
            ans.push_back(b[i]);
        } else {
            for (int j = 0; j < a[b[i]] - 1; j++) {
                ans.push_back(b[i]);
            }
            ans.push_back(b[i]);
        }
    }

    cout << ans.size() << endl;
    for (int i = 0; i < ans.size(); i++) {
        cout << ans[i] + 1 << " ";
    }
    cout << endl;

    return 0;
}
