#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 1000000007
#define MAXN 20

typedef long long ll;

ll dp[MAXN][1 << 15];
ll sum[1 << 15];

int main() {
    int n, q;
    scanf("%d%d", &n, &q);
    int l[q + 1], r[q + 1];
    for (int i = 0; i < q; i++) {
        scanf("%d%d", &l[i], &r[i]);
    }
    memset(dp, 0, sizeof(dp));
    dp[0][0] = 1;
    for (int i = 0; i < q; i++) {
        for (int j = 0; j < (1 << q); j++) {
            sum[j] = (sum[j] + dp[i][j]) % MOD;
        }
        for (int j = 0; j < (1 << q); j++) {
            if (j & (1 << i)) continue;
            for (int k = 0; k < 10; k++) {
                int x = (j | (1 << i));
                if ((l[i] - 1) + k <= r[i]) {
                    dp[i + 1][x] = (dp[i + 1][x] + sum[j] * (k % 9 == 0)) % MOD;
                }
            }
        }
    }
    ll ans = 0;
    for (int i = 0; i < (1 << q); i++) {
        ans = (ans + dp[q][i]) % MOD;
    }
    for (int i = 0; i < n; i++) {
        ans = (ans * 10) % MOD;
    }
    printf("%lld\n", ans);
    return 0;
}
