#include <iostream>
#include <vector>
#include <algorithm>
#include <cstring>

using namespace std;

const int N = 20;
const int M = 1e9 + 7;

int n, q;
int l[N], r[N];
int dp[N][1 << N];
int pw[N];

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    cin >> n >> q;
    for (int i = 0; i < q; i++) {
        cin >> l[i] >> r[i];
        l[i]--;
    }

    pw[0] = 1;
    for (int i = 1; i <= n; i++) {
        pw[i] = (pw[i - 1] * 10) % M;
    }

    for (int i = 0; i <= n; i++) {
        dp[0][1 << i] = 1;
    }

    for (int i = 0; i < q; i++) {
        for (int mask = 0; mask < (1 << n); mask++) {
            for (int j = 0; j <= n; j++) {
                if (dp[i][mask] == 0) continue;
                int x = (mask >> (l[i] - j)) & ((1 << (r[i] - l[i] + 1)) - 1);
                int y = (mask >> (r[i] - j)) & ((1 << (n - r[i] + 1)) - 1);
                int z = (mask >> (n - j)) & ((1 << (l[i] - n + 1)) - 1);
                dp[i + 1][mask] = (dp[i + 1][mask] + dp[i][mask] * pw[r[i] - l[i] + 1]) % M;
                if (x == 0) {
                    dp[i + 1][mask | (1 << (l[i] - j))] = (dp[i + 1][mask | (1 << (l[i] - j))] + dp[i][mask]) % M;
                } else if (x % 9 == 0) {
                    dp[i + 1][mask | (1 << (l[i] - j))] = (dp[i + 1][mask | (1 << (l[i] - j))] + dp[i][mask] * (9 - x) % M) % M;
                }
                if (y == 0) {
                    dp[i + 1][mask | (1 << (r[i] - j))] = (dp[i + 1][mask | (1 << (r[i] - j))] + dp[i][mask]) % M;
                } else if (y % 9 == 0) {
                    dp[i + 1][mask | (1 << (r[i] - j))] = (dp[i + 1][mask | (1 << (r[i] - j))] + dp[i][mask] * (9 - y) % M) % M;
                }
                if (z == 0) {
                    dp[i + 1][mask | (1 << (n - j))] = (dp[i + 1][mask | (1 << (n - j))] + dp[i][mask]) % M;
                } else if (z % 9 == 0) {
                    dp[i + 1][mask | (1 << (n - j))] = (dp[i + 1][mask | (1 << (n - j))] + dp[i][mask] * (9 - z) % M) % M;
                }
            }
        }
    }

    int ans = 0;
    for (int mask = 0; mask < (1 << n); mask++) {
        ans = (ans + dp[q][mask]) % M;
    }

    cout << ans << '\n';

    return 0;
}
