#include <iostream>
#include <vector>
#include <algorithm>

struct Reservation {
    int start;
    int end;
};

bool compareReservations(const Reservation& a, const Reservation& b) {
    return a.start < b.start;
}

int main() {
    int newStart, newEnd;
    std::cin >> newStart >> newEnd;

    int n;
    std::cin >> n;

    std::vector<Reservation> reservations(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> reservations[i].start >> reservations[i].end;
    }

    std::sort(reservations.begin(), reservations.end(), compareReservations);

    bool overlap = false;
    for (int i = 0; i < n; ++i) {
        if (reservations[i].start < newEnd && reservations[i].end > newStart) {
            overlap = true;
            break;
        }
    }

    std::cout << (overlap ? 1 : 0) << std::endl;

    return 0;
}
