#include <stdio.h>
#include <math.h>
#include <string.h>

#define MOD 1000000007
#define N 1000

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return power(a, MOD - 2);
}

int main() {
    int n;
    char s[N + 1];
    scanf("%d %s", &n, s);
    int cnt = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'X') cnt++;
    }
    long long a = 1, b = 0, c = 0;
    for (int i = 0; i < n; i++) {
        if (s[i] == 'X') {
            long long p = (a + b * power(2, i)) % MOD;
            long long q = (a + b * power(2, i) + c * power(2, i + 1)) % MOD;
            a = p;
            b = q;
        } else {
            long long p = (a + b * power(2, i)) % MOD;
            long long q = (a + b * power(2, i) + c * power(2, i + 1)) % MOD;
            long long r = (a + b * power(2, i) + c * power(2, i + 1) + 1) % MOD;
            a = p;
            b = q;
            c = r;
        }
    }
    long long p = (a + b * power(2, n)) % MOD;
    long long q = (a + b * power(2, n) + c * power(2, n + 1)) % MOD;
    long long r = (a + b * power(2, n) + c * power(2, n + 1) + 1) % MOD;
    long long x = (power(2, n) + 1) % MOD;
    long long y = (p * inv(x) % MOD + MOD) % MOD;
    printf("%lld %lld %lld\n", y, q * inv(x) % MOD, r * inv(x) % MOD);
    return 0;
}
