import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int a = scanner.nextInt();
        int b = scanner.nextInt();
        int n = scanner.nextInt();
        int[][] reservations = new int[n][2];

        for (int i = 0; i < n; i++) {
            reservations[i][0] = scanner.nextInt();
            reservations[i][1] = scanner.nextInt();
        }

        Arrays.sort(reservations, (x, y) -> x[0] - y[0]);

        int i = 0;
        while (i < n && reservations[i][1] <= a) {
            i++;
        }

        if (i > 0 && reservations[i - 1][1] > a) {
            System.out.println("1");
        } else if (i < n && reservations[i][0] < b) {
            System.out.println("1");
        } else {
            System.out.println("0");
        }
    }
}
