#include <iostream>
#include <vector>
#include <string>
#include <cmath>
#include <numeric>

using namespace std;

const int MOD = 1e9 + 7;

long long power(long long a, long long b) {
    long long res = 1;
    while (b) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
        b >>= 1;
    }
    return res;
}

long long inv(long long a) {
    return power(a, MOD - 2);
}

int main() {
    int N;
    string s;
    cin >> N >> s;

    vector<int> cnt(N + 1);
    for (int i = 0; i < N; i++) {
        cnt[i + 1] = cnt[i] + (s[i] == 'X');
    }

    vector<int> dp(N + 1, 0);
    dp[0] = 1;
    for (int i = 0; i < N; i++) {
        for (int j = 0; j <= i; j++) {
            if (s[i] == 'X') {
                dp[i + 1] = (dp[i + 1] + dp[j] * cnt[i - j + 1] % MOD * inv(cnt[i - j + 1] - 1)) % MOD;
            } else {
                dp[i + 1] = (dp[i + 1] + dp[j] * (cnt[i - j] - 1) % MOD * inv(cnt[i - j])) % MOD;
            }
        }
    }

    long long p = 0;
    long long q = 0;
    long long r = 0;
    for (int i = 0; i <= N; i++) {
        long long x = dp[i];
        long long y = power(2, i) - 1;
        long long z = power(2, N - i);
        long long t = (x * y % MOD * inv(z) + MOD) % MOD;
        p = (p + t) % MOD;
        q = (q + x * y % MOD * inv(z) * (N - i) % MOD) % MOD;
        r = (r + x * (i + 1) % MOD * inv(z) % MOD * (N - i) % MOD) % MOD;
    }

    cout << p << " " << q << " " << r << endl;

    return 0;
}
