public class Main {
    static final int MOD = 1000000007;
    static final int MAXN = 1005;

    static long[] fact = new long[MAXN];
    static long[] inv = new long[MAXN];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);

        int n = Integer.parseInt(br.readLine());
        String s = br.readLine();

        long p = 0;
        long q = 0;
        long r = 0;

        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == 'X') {
                p++;
                long a = 1;
                long b = 1;
                for (int j = 0; j < i; j++) {
                    a = (a * (i - j)) % MOD;
                    b = (b * (j + 1)) % MOD;
                }
                long c = inv[i];
                long d = inv[n - i - 1];
                long e = inv[n - i];
                long f = inv[n - 1];
                long g = (a * c) % MOD;
                long h = (b * d) % MOD;
                long i1 = (g * h) % MOD;
                long j1 = (e * f) % MOD;
                long k1 = (i1 * j1) % MOD;
                q = (q + k1) % MOD;
                long l1 = 1;
                for (int j = 0; j < i; j++) {
                    l1 = (l1 * (j + 1)) % MOD;
                }
                long m1 = inv[n - 1];
                long n1 = (l1 * m1) % MOD;
                long o1 = (n1 * k1) % MOD;
                r = (r + o1) % MOD;
            }
        }

        long a = 1;
        long b = 1;
        for (int i = 0; i < n; i++) {
            a = (a * (i + 1)) % MOD;
            b = (b * (i + 1)) % MOD;
        }
        long c = inv[n];
        long d = inv[n];
        long e = (a * c) % MOD;
        long f = (b * d) % MOD;
        long g = (e * f) % MOD;
        long h = inv[2];
        long i = (g * h) % MOD;
        long j = (i * h) % MOD;
        q = (q + j) % MOD;
        long k = inv[2];
        long l = (k * j) % MOD;
        r = (r + l) % MOD;

        long m = 1;
        for (int i = 0; i < n; i++) {
            m = (m * (i + 1)) % MOD;
        }
        long n1 = inv[n];
        long o1 = (m * n1) % MOD;
        p = (p + o1) % MOD;

        pw.println(p);
        pw.println(q);
        pw.println(r);

        pw.close();
    }

    static long pow(long a, long b) {
        long res = 1;
        while (b > 0) {
            if ((b & 1) == 1) {
                res = (res * a) % MOD;
            }
            a = (a * a) % MOD;
            b >>= 1;
        }
        return res;
    }

    static long inv(long a) {
        return pow(a, MOD - 2);
    }

    static long C(int n, int k) {
        return (fact[n] * inv[k] % MOD) * inv[n - k] % MOD;
    }
}
