import math
import sys

MOD = 10**9 + 7

def inv(x):
    return pow(x, MOD - 2, MOD)

def C(n, k):
    return math.comb(n, k) % MOD

def solve():
    N = int(input())
    s = input()

    p = 0
    for i in range(N):
        if s[i] == 'X':
            p += 1

    p = p % MOD
    q = 0
    r = 0

    for i in range(N):
        if s[i] == 'X':
            for j in range(i + 2, N):
                if s[j] == 'X':
                    q += C(N - 1, j - i - 1) * C(i - 1, j - 2) * C(N - j - 1, N - 3 - j + i)
                    q %= MOD
            for j in range(i - 1, -1, -1):
                if s[j] == 'X':
                    q += C(N - 1, i - j - 1) * C(j + 1, i - 1) * C(N - i - 1, N - 2 - i + j)
                    q %= MOD
            for j in range(i + 2, N):
                if s[j] == 'X':
                    r += C(N - 1, j - i - 1) * C(i - 1, j - 2) * C(N - j - 1, N - 3 - j + i)
                    r %= MOD
            for j in range(i - 1, -1, -1):
                if s[j] == 'X':
                    r += C(N - 1, i - j - 1) * C(j + 1, i - 1) * C(N - i - 1, N - 2 - i + j)
                    r %= MOD
        else:
            for j in range(i + 2, N):
                if s[j] == 'X':
                    q += C(N - 1, j - i - 1) * C(i - 1, j - 2) * C(N - j - 1, N - 3 - j + i)
                    q %= MOD
            for j in range(i - 1, -1, -1):
                if s[j] == 'X':
                    q += C(N - 1, i - j - 1) * C(j + 1, i - 1) * C(N - i - 1, N - 2 - i + j)
                    q %= MOD
            for j in range(i + 2, N):
                if s[j] == 'X':
                    r += C(N - 1, j - i - 1) * C(i - 1, j - 2) * C(N - j - 1, N - 3 - j + i)
                    r %= MOD
            for j in range(i - 1, -1, -1):
                if s[j] == 'X':
                    r += C(N - 1, i - j - 1) * C(j + 1, i - 1) * C(N - i - 1, N - 2 - i + j)
                    r %= MOD

    q = (q * inv(2)) % MOD
    r = (r * inv(2)) % MOD

    print(p, q, r)

if __name__ == '__main__':
    solve()
