#include <stdio.h>
#include <stdlib.h>

int main() {
    int n, m;
    scanf("%d %d", &n, &m);

    int** likes = (int**)malloc(n * sizeof(int*));
    for (int i = 0; i < n; i++) {
        int k;
        scanf("%d", &k);
        likes[i] = (int*)malloc((k + 1) * sizeof(int));
        likes[i][0] = k;
        for (int j = 1; j <= k; j++) {
            scanf("%d", &likes[i][j]);
        }
    }

    int* count = (int*)malloc((m + 1) * sizeof(int));
    for (int i = 1; i <= m; i++) {
        count[i] = 1;
    }

    for (int i = 0; i < n; i++) {
        for (int j = 1; j <= likes[i][0]; j++) {
            count[likes[i][j]] = 0;
        }
    }

    int result = 0;
    for (int i = 1; i <= m; i++) {
        if (count[i] == 1) {
            result++;
        }
    }

    printf("%d\n", result);

    for (int i = 0; i < n; i++) {
        free(likes[i]);
    }
    free(likes);
    free(count);

    return 0;
}
