#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX 10000

int matchsticks[10] = {2, 5, 5, 4, 5, 6, 3, 7, 6};
char max_num[10000];

void dfs(int n, int m, int used, int idx) {
    if (idx == m) {
        int i;
        int sum = 0;
        for (i = 0; i < n; i++) {
            sum += matchsticks[max_num[i] - '0'];
        }
        if (sum == used) {
            for (i = 0; i < n; i++) {
                printf("%c", max_num[i]);
            }
            printf("\n");
        }
        return;
    }
    for (int i = 0; i <= 9; i++) {
        if (i != 0 || idx == 0) {
            max_num[n] = i + '0';
            dfs(n + 1, m, used, idx + 1);
        }
    }
}

int main() {
