import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int m = scanner.nextInt();
        int[] a = new int[m];
        for (int i = 0; i < m; i++) {
            a[i] = scanner.nextInt();
        }
        Arrays.sort(a);
        int[] matchsticks = {2, 5, 5, 4, 5, 6, 3, 7, 6};
        int[] digits = new int[9];
        for (int i = 0; i < 9; i++) {
            digits[i] = -1;
        }
        for (int i = 0; i < m; i++) {
            digits[a[i] - 1] = a[i];
        }
        int max = 0;
        for (int i = 0; i < (1 << m); i++) {
            int num = 0;
            int count = 0;
            for (int j = 0; j < m; j++) {
                if ((i & (1 << j)) != 0) {
                    count += matchsticks[a[j] - 1];
                    num = num * 10 + digits[a[j] - 1];
                }
            }
            if (count <= n && count > max) {
                max = count;
                System.out.print(num);
            }
        }
        while (n > max) {
            int maxDigit = -1;
            for (int i = 0; i < m; i++) {
                if (digits[i] != -1 && matchsticks[digits[i] - 1] <= n - max) {
                    maxDigit = digits[i];
                    break;
                }
            }
            if (maxDigit != -1) {
                System.out.print(maxDigit);
                max += matchsticks[maxDigit - 1];
            }
        }
        while (n > max) {
            System.out.print(9);
            max += 6;
        }
        System.out.println();
    }
}
