def largest_number(n, m, A):
    A = sorted(A, reverse=True)
    max_num = ''
    for i in range(m):
        for j in range(i, m):
            for k in range(j, m):
                for l in range(k, m):
                    for r in range(l, m):
                        for s in range(r, m):
                            for t in range(s, m):
                                for u in range(t, m):
                                    for v in range(u, m):
                                        for w in range(v, m):
                                            for x in range(w, m):
                                                for y in range(x, m):
                                                    for z in range(y, m):
                                                        num = A[i] + A[j] + A[k] + A[l] + A[r] + A[s] + A[t] + A[u] + A[v] + A[w] + A[x] + A[y] + A[z]
                                                        if sum(matchsticks[digit] for digit in num) <= n:
                                                            max_num = max(max_num, num)
    return max_num

n, m = map(int, input().split())
A = list(map(int, input().split()))
print(largest_number(n, m, A))
