#include <stdio.h>
#include <stdlib.h>

#define MAX_ROADS 3
#define MAX_TOWNS 4

int main() {
    int roads[MAX_ROADS][2];
    int visited[MAX_TOWNS] = {0};
    int i, j, k, town, edge, count = 0;

    for (i = 0; i < MAX_ROADS; i++) {
        scanf("%d %d", &roads[i][0], &roads[i][1]);
    }

    for (i = 0; i < MAX_ROADS; i++) {
        for (j = 0; j < 2; j++) {
            town = roads[i][j];
            if (visited[town - 1] == 0) {
                visited[town - 1] = 1;
                count++;
            }
        }
    }

    if (count == MAX_TOWNS) {
        for (i = 0; i < MAX_ROADS; i++) {
            for (j = 0; j < 2; j++) {
                town = roads[i][j];
                for (k = 0; k < MAX_ROADS; k++) {
                    if (k != i) {
                        edge = roads[k][j];
                        if (edge == town) {
                            printf("NO\n");
                            return 0;
                        }
                    }
                }
            }
        }
        printf("YES\n");
    } else {
        printf("NO\n");
    }

    return 0;
}
