#include <iostream>
#include <vector>
#include <unordered_map>
#include <set>

using namespace std;

int main() {
    int n;
    cin >> n;
    vector<pair<int, int>> roads(n);
    for (int i = 0; i < n; i++) {
        cin >> roads[i].first >> roads[i].second;
    }

    unordered_map<int, vector<int>> graph;
    for (int i = 0; i < n; i++) {
        graph[roads[i].first].push_back(roads[i].second);
        graph[roads[i].second].push_back(roads[i].first);
    }

    set<int> visited;
    int count = 0;
    for (int i = 1; i <= 4; i++) {
        if (graph[i].size() == 2) {
            count++;
            visited.insert(i);
            int u = i;
            while (graph[u].size() == 2) {
                visited.insert(u);
                for (int v : graph[u]) {
                    if (visited.find(v) == visited.end()) {
                        u = v;
                        break;
                    }
                }
            }
        }
    }

    if (count == 2 && visited.size() == 4) {
        cout << "YES" << endl;
    } else {
        cout << "NO" << endl;
    }

    return 0;
}
