#include <stdio.h>
#include <stdlib.h>
#include <math.h>

int main() {
    long long k, a, b;
    scanf("%lld %lld %lld", &k, &a, &b);

    long long ans = 1 + (k - 1) * a;
    long long x = (ans - 1) / a;
    long long y = (ans - 1) % a;

    long long max_biscuits = 1 + x * b + y;
    printf("%lld\n", max_biscuits);

    return 0;
}
