#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int l;
    int* a;
} input;

long long int min(long long int a, long long int b) {
    return (a < b) ? a : b;
}

long long int solve(input* in) {
    long long int i, j, k, l, sum, ans = 0;
    l = in->l;
    int* a = in->a;

    for (i = 0; i < l; i++) {
        ans += abs(a[i]);
    }

    for (i = 0; i < l; i++) {
        if (a[i] == 0) {
            continue;
        }
        for (j = i + 1; j < l; j++) {
            if (a[j] == 0) {
                continue;
            }
            if (a[i] < a[j]) {
                k = a[i];
                a[i] = a[j];
                a[j] = k;
            }
        }
    }

    for (i = 0; i < l - 1; i++) {
        ans -= min(a[i], a[i + 1]);
    }

    return ans;
}

int main() {
    int i, j, k, l, n;
    input in;
    scanf("%d", &in.l);
    in.a = (int*)malloc(in.l * sizeof(int));
    for (i = 0; i < in.l; i++) {
        scanf("%d", &in.a[i]);
    }
    printf("%lld\n", solve(&in));
    free(in.a);
    return 0;
}
