#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MOD 998244353
#define MAXN 310

typedef long long ll;

int n, m;
int a[MAXN][MAXN];
int dp[2][1 << MAXN];
int sum[2][1 << MAXN];

int add(int x, int y) {
    x += y;
    if (x >= MOD) x -= MOD;
    return x;
}

int sub(int x, int y) {
    x -= y;
    if (x < 0) x += MOD;
    return x;
}

int mul(int x, int y) {
    return (ll)x * y % MOD;
}

int main() {
    scanf("%d %d", &n, &m);
    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            scanf("%d", &a[i][j]);
        }
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            dp[0][1 << (i - 1)] = add(dp[0][1 << (i - 1)], a[i][j]);
            dp[1][1 << (j - 1)] = add(dp[1][1 << (j - 1)], a[i][j]);
        }
    }

    for (int i = 1; i <= n; i++) {
        for (int j = 1; j <= m; j++) {
            dp[0][1 << (i - 1)] = add(dp[0][1 << (i - 1)], sub(MOD, a[i][j]));
            dp[1][1 << (j - 1)] = add(dp[1][1 << (j - 1)], sub(MOD, a[i][j]));
        }
    }

    for (int i = 0; i < (1 << n); i++) {
        for (int j = 0; j < (1 << m); j++) {
            sum[0][i] = add(sum[0][i], mul(dp[0][i], dp[1][j]));
            sum[1][i] = add(sum[1][i], mul(dp[1][j], dp[0][i]));
        }
    }

    int ans = 0;
    for (int i = 0; i < (1 << n); i++) {
        for (int j = 0; j < (1 << m); j++) {
            if ((sum[0][i] + sum[1][j]) % 2 == 1) {
                ans = add(ans, mul(dp[0][i], dp[1][j]));
            }
        }
    }

    printf("%d\n", ans);

    return 0;
}
