#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

const int MOD = 998244353;

int add(int a, int b) {
    return (a + b) % MOD;
}

int sub(int a, int b) {
    return (a - b + MOD) % MOD;
}

int mul(int a, int b) {
    return (int)((long long)a * b % MOD);
}

int binpow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = mul(res, a);
        a = mul(a, a);
        b >>= 1;
    }
    return res;
}

int inv(int a) {
    return binpow(a, MOD - 2);
}

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> a(n, vector<int>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> a[i][j];
        }
    }

    vector<int> dp(n + 1, 0);
    dp[0] = 1;
    for (int i = 0; i < n; ++i) {
        vector<int> ndp(n + 1, 0);
        for (int j = 0; j <= i; ++j) {
            ndp[j] = add(ndp[j], dp[j]);
            if (a[i][0]) {
                ndp[j + 1] = add(ndp[j + 1], dp[j]);
            }
        }
        dp = ndp;
    }

    vector<int> dp2(m + 1, 0);
    dp2[0] = 1;
    for (int i = 0; i < m; ++i) {
        vector<int> ndp2(m + 1, 0);
        for (int j = 0; j <= i; ++j) {
            ndp2[j] = add(ndp2[j], dp2[j]);
            if (a[0][i]) {
                ndp2[j + 1] = add(ndp2[j + 1], dp2[j]);
            }
        }
        dp2 = ndp2;
    }

    int ans = 0;
    for (int i = 0; i <= n; ++i) {
        for (int j = 0; j <= m; ++j) {
            if ((i & 1) ^ (j & 1)) {
                ans = add(ans, mul(dp[i], dp2[j]));
            }
        }
    }

    cout << ans << endl;

    return 0;
}
