import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static int[][] a;
    static int[][] dp;
    static int[][] sum;

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        StringTokenizer st = new StringTokenizer(br.readLine());

        int n = Integer.parseInt(st.nextToken());
        int m = Integer.parseInt(st.nextToken());

        a = new int[n + 1][m + 1];
        dp = new int[n + 1][m + 1];
        sum = new int[n + 1][m + 1];

        for (int i = 1; i <= n; i++) {
            st = new StringTokenizer(br.readLine());
            for (int j = 1; j <= m; j++) {
                a[i][j] = Integer.parseInt(st.nextToken());
            }
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                sum[i][j] = sum[i - 1][j] + sum[i][j - 1] - sum[i - 1][j - 1] + a[i][j];
            }
        }

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= m; j++) {
                dp[i][j] = (int) ((2L * dp[i - 1][j] + 2L * dp[i][j - 1] - 4L * dp[i - 1][j - 1] + (a[i][j] == 1 ? 1 : 0)) % MOD);
            }
        }

        int ans = 0;
        for (int i = 0; i <= n; i++) {
            for (int j = 0; j <= m; j++) {
                int x = (i + j) % 2;
                int y = (n - i + m - j) % 2;
                ans = (int) ((ans + 1L * dp[i][j] * (1L << (n - i + m - j)) % MOD * (1L << (i + j)) % MOD) % MOD);
            }
        }

        pw.println(ans);
        pw.close();
    }
}
