#include <stdio.h>
#include <string.h>

const int mod = 998244353;

int add(int a, int b) {
    return (a + b) % mod;
}

int mul(int a, int b) {
    return (long long)a * b % mod;
}

int pow(int a, int b) {
    int res = 1;
    while (b) {
        if (b & 1) res = mul(res, a);
        a = mul(a, a);
        b >>= 1;
    }
    return res;
}

int main() {
    char s[2005];
    scanf("%s", s);
    int n = strlen(s);
    int dp[2005][3][3] = {{0}};
    dp[0][0][0] = 1;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j <= i; j++) {
            for (int k = 0; k <= 2; k++) {
                if (s[i] == '0') {
                    dp[j + 1][k][0] = add(dp[j + 1][k][0], mul(dp[j][k][0], 2));
                    dp[j + 1][k][1] = add(dp[j + 1][k][1], mul(dp[j][k][0], 2));
                    dp[j + 1][k][2] = add(dp[j + 1][k][2], dp[j][k][0]);
                } else if (s[i] == '1') {
                    dp[j + 1][k][0] = add(dp[j + 1][k][0], mul(dp[j][k][0], 2));
                    dp[j + 1][k][1] = add(dp[j + 1][k][1], mul(dp[j][k][0], 2));
                    dp[j + 1][k][2] = add(dp[j + 1][k][2], dp[j][k][0]);
                    dp[j + 1][k + 1][0] = add(dp[j + 1][k + 1][0], mul(dp[j][k][0], 1));
                    dp[j + 1][k + 1][1] = add(dp[j + 1][k + 1][1], mul(dp[j][k][0], 1));
                    dp[j + 1][k + 1][2] = add(dp[j + 1][k + 1][2], dp[j][k][0]);
                } else {
                    dp[j + 1][k][0] = add(dp[j + 1][k][0], mul(dp[j][k][0], 2));
                    dp[j + 1][k][1] = add(dp[j + 1][k][1], mul(dp[j][k][0], 1));
                    dp[j + 1][k][2] = add(dp[j + 1][k][2], dp[j][k][0]);
                    dp[j + 1][k + 1][0] = add(dp[j + 1][k + 1][0], mul(dp[j][k][0], 1));
                    dp[j + 1][k + 1][1] = add(dp[j + 1][k + 1][1], mul(dp[j][k][0], 1));
                    dp[j + 1][k + 1][2] = add(dp[j + 1][k + 1][2], dp[j][k][0]);
                }
            }
        }
    }
    int ans = 0;
    for (int i = 0; i <= n; i++) {
        for (int j = 0; j <= 2; j++) {
            ans = add(ans, dp[n][i][j]);
        }
    }
    printf("%d\n", ans);
    return 0;
}
