#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

const int mod = 998244353;

long long powmod(long long a, long long b) {
    long long res = 1;
    a %= mod;
    for (; b; b >>= 1) {
        if (b & 1) res = res * a % mod;
        a = a * a % mod;
    }
    return res;
}

int main() {
    string s;
    cin >> s;
    int n = s.size();
    vector<vector<long long>> dp(n + 1, vector<long long>(3));
    dp[0][0] = 1;
    for (int i = 0; i < n; i++) {
        vector<vector<long long>> ndp(n + 1, vector<long long>(3));
        for (int j = 0; j <= i; j++) {
            for (int k = 0; k <= 2; k++) {
                if (s[i] == '0') {
                    ndp[j + 1][0] = (ndp[j + 1][0] + dp[j][k]) % mod;
                    ndp[j][1] = (ndp[j][1] + dp[j][k]) % mod;
                } else if (s[i] == '1') {
                    ndp[j + 1][0] = (ndp[j + 1][0] + dp[j][k]) % mod;
                    ndp[j][0] = (ndp[j][0] + dp[j][k]) % mod;
                    ndp[j][1] = (ndp[j][1] + dp[j][k]) % mod;
                } else {
                    ndp[j][0] = (ndp[j][0] + dp[j][k]) % mod;
                    ndp[j][1] = (ndp[j][1] + dp[j][k]) % mod;
                    ndp[j + 1][1] = (ndp[j + 1][1] + dp[j][k]) % mod;
                }
            }
        }
        dp = ndp;
    }
    long long ans = 0;
    for (int i = 0; i <= n; i++) {
        ans = (ans + dp[i][1] * powmod(2, i)) % mod;
    }
    cout << ans << endl;
    return 0;
}
