import java.io.*;
import java.util.*;

public class Main {
    static final int MOD = 998244353;
    static final int MAXN = 2000;

    static long[][] dp = new long[MAXN + 1][3];
    static long[] pre = new long[MAXN + 1];

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        PrintWriter pw = new PrintWriter(System.out);
        String s = br.readLine();
        int n = s.length();
        dp[0][0] = 1;
        for (int i = 0; i < n; i++) {
            for (int j = 0; j <= i; j++) {
                pre[j] = (pre[j] + dp[i][j]) % MOD;
                if (s.charAt(i) == '0') {
                    dp[i + 1][0] = (dp[i + 1][0] + dp[i][j]) % MOD;
                } else if (s.charAt(i) == '1') {
                    dp[i + 1][0] = (dp[i + 1][0] + dp[i][j]) % MOD;
                    dp[i + 1][1] = (dp[i + 1][1] + dp[i][j]) % MOD;
                } else {
                    dp[i + 1][0] = (dp[i + 1][0] + dp[i][j]) % MOD;
                    dp[i + 1][1] = (dp[i + 1][1] + dp[i][j]) % MOD;
                    dp[i + 1][2] = (dp[i + 1][2] + dp[i][j]) % MOD;
                }
            }
            for (int j = 0; j <= i; j++) {
                dp[i + 1][j] = (dp[i + 1][j] + pre[j]) % MOD;
            }
        }
        long ans = 0;
        for (int i = 0; i <= n; i++) {
            ans = (ans + dp[n][i]) % MOD;
        }
        pw.println(ans);
        pw.close();
    }
}
