#include <iostream>
#include <vector>
#include <algorithm>

int main() {
    int n;
    std::cin >> n;

    std::vector<int> sides(n);
    for (int i = 0; i < n; ++i) {
        std::cin >> sides[i];
    }

    std::sort(sides.begin(), sides.end());

    if (sides[n - 1] < sides[0] + sides[1] + sides[2]) {
        std::cout << "Yes";
    } else {
        std::cout << "No";
    }

    return 0;
}
