#include <stdio.h>
#include <stdlib.h>
#include <math.h>

#define MAXN 100005

int main() {
    int N, M;
    scanf("%d %d", &N, &M);
    int X[MAXN];
    for (int i = 0; i < M; i++) {
        scanf("%d", &X[i]);
    }
    int min = 0, max = 0;
    for (int i = 0; i < M; i++) {
        if (X[i] < min) min = X[i];
        if (X[i] > max) max = X[i];
    }
    int moves = 0;
    for (int i = min; i < max; i++) {
        int left = 0, right = 0;
        for (int j = 0; j < M; j++) {
            if (X[j] < i) left = i - X[j];
            if (X[j] > i) right = X[j] - i;
        }
        if (left == 0 && right == 0) continue;
        if (left == 0) moves += right;
        else if (right == 0) moves += left;
        else moves += left + right;
    }
    printf("%d\n", moves);
    return 0;
}
