import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        int m = scanner.nextInt();
        int[] x = new int[m];
        for (int i = 0; i < m; i++) {
            x[i] = scanner.nextInt();
        }
        Arrays.sort(x);
        int[] min = new int[m];
        min[0] = Math.abs(x[0] - 0);
        for (int i = 1; i < m; i++) {
            min[i] = Math.min(Math.abs(x[i] - x[i - 1]), Math.min(Math.abs(x[i] - x[0]) + Math.abs(x[i - 1] - x[0]), Math.abs(x[i] - x[i - 1]) + Math.abs(x[0] - x[i - 1])));
        }
        int sum = 0;
        for (int i = 0; i < m; i++) {
            sum += min[i];
        }
        System.out.println(sum);
    }
}
