#include <stdio.h>
#include <stdlib.h>

int max(int a, int b) {
    return (a > b) ? a : b;
}

int max_xor(int* arr, int n, int k) {
    int max_val = 0;
    for (int i = 0; i < n; i++) {
        int xor = 0;
        for (int j = 0; j < n; j++) {
            if (i != j) {
                xor ^= arr[j];
            }
        }
        max_val = max(max_val, xor);
    }
    return max_val;
}

