    int maxVal = 0;
    for (int i = 0; i < n; i++) {
        maxVal = std::max(maxVal, nums[i]);
    }
    int maxRes = 0;
    for (int i = 0; i < n; i++) {
        int res = 0;
        for (int j = 0; j < 32; j++) {
            if ((maxVal >> j) & 1) {
                if ((nums[i] >> j) & 1) {
                    res += (1 << j);
                } else {
                    res += (1 << (j + 1)) - 1;
                }
            } else {
